import pdfMake from "pdfmake/build/pdfmake";
import pdfFonts from "pdfmake/build/vfs_fonts";

pdfMake.vfs = pdfFonts.pdfMake.vfs; // Set fonts for pdfmake

const formatData = (itineraries)=>{
    const formattedContent = [];
    itineraries.forEach((itinerary, i)=>{
        formattedContent.push( {
            stack:[
                {
                    text: itineraries[i]?.title,
                    fontSize: 20,
                    bold: true
                },
                {
                    table: {
                      widths: ["50%", "50%"],
                      body: [
                        [
                          {
                            text: itineraries[i]?.date,
                           fontSize: 18,
                           bold: true,
                           alignment: "left",
                          },
                          {
                            // width: "*",
                            alignment: "right",
                            fontSize: 18,
                            bold: true,
                            text: itineraries[i]?.budget  
                          },
                        ],
                      ],
                    },
                    layout: "noBorders",
                },
                {
                    text: itineraries[i]?.text,
                    fontSize: 14,
                }
            ],
            margin:[0,20,0,20],
          })
    })

    return formattedContent;
}

export function generatePdf(jsonData, dates) {
  console.log("jsoin data", jsonData, __dirname);
  let stackContent = formatData(jsonData?.itineraries)

  var docDefinition = {  
    content: [
      {
        table: {
          widths: ["auto", "*"],
          body: [
            [
              {
                image: logo_base64,
                width: 75,
                height: 75,
              },
              {
                width: "*",
                alignment: "center",
                stack: [
                  {
                    style: 'h1',
                    text: jsonData?.title
                  },
                  {
                    style: 'h2',
                    text: "AI generated itinierary by HADANA",
                  },
                ],
              },
            ],
          ],
        },
        layout: "noBorders",
      },
      ...stackContent
    ],
    styles: {
        h1: {
            margin: [0, 10, 0, 0],
            fontSize: 24,
            bold: true
        },
        h2: {
            margin: [0, 10, 0, 0],
            fontSize: 10,
        }
    }
  };

  pdfMake.createPdf(docDefinition).download(); // Triggers download
}

const logo_base64 =
  "data:image/png;base64,";
